export const initialState = {
  basket: [],
};

//selector
basket.reduce((amount, item) => ({ price: item.price + amount.price }));

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "add_to_basket":
      return {
        ...state,
        basket: [...state.basket, action.item],
      };
    default:
      return state;
  }
};

export default reducer;
