export const initialState = {
  basket: [],
};

//selector
export const getbaskettotal = (basket) =>
  basket.map((x) => x.price).reduce((amount, item) => amount + item);

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "add_to_basket":
      return {
        ...state,
        basket: [...state.basket, action.item],
      };
    default:
      return state;
  }
};

export default reducer;
